import React, { Component } from "react";

export default class TodoItem extends Component {
  render() {
    const { title, handleDelete, handleEdit, handleDone,backgroundColor } = this.props;
    return (
      <li className="list-group-item text-capitalize d-flex justify-content-between my-2"   style={{backgroundColor: bgColor}}>
        <h6>{title}</h6>
        <div className="todo-icon">
          <span className="mx-2 text-success" onClick={handleEdit}>
            <i className="fas fa-pen" />
          </span>
          <span className="mx-2 text-danger" onClick={handleDelete}>
            <i className="fas fa-trash" />
          </span>
          <span className="mx-2 text-primary">
            <i className="fas fa-check" onClick={handleDone} />
          </span>
        </div>
      </li>
    );
  }
}
